       await fetch('http://127.0.0.1/verifyjwt',options).then(res=>res.json()).then(data=>{
        if(userstate.verify=="false"){
            navigate("/")
        }    
       setuser(data)
            
        })
   
    }
    else
    navigate("/")
}

if(userstate.userid)
